import React, { useEffect, useState } from 'react';

function MyComponent() {
    const [qrCodeData, setQrCodeData] = useState(null);  // State to store QR code image data

    useEffect(() => {
        const loadPyodide = async () => {
            const pyodide = await loadPyodide();

            // Access Python modules from your files
            const qrData = await pyodide.runPython('import qr_data; from qr_generator import generate_qr_code; return generate_qr_code(qr_data.get_data())');

            setQrCodeData(qrData); // Update state with QR code data
        };

        loadPyodide();
    }, []);

    // ... (rest of your component logic)

    return (
        <div>

        </div>
    );
}

function QRCodeGen() {
    const [text, setText] = useState('')
    const [radio, setRadio] = useState('a')

    const handleText = (event) => {
        setText(event.target.value)
    }
    const handleRadio = (event) => {
        setRadio(event.target.value)
    }

    const generateQR = async () => {
        setRadio(event.target.value)
    }

    return (
        <div className = "px-[10%] py-10">
            <h1 className="font-black text-4xl">QR Code Generator</h1>
            <p>Short description</p>
            <div className="text-center">
                <input type="text" value={text} onChange={handleText}></input>
                <label>
                    <input type="radio" value="text" checked={radio === 'text'} onChange={handleRadio} />
                    Text
                </label>
                <label>
                    <input type="radio" value="url" checked={radio === 'url'} onChange={handleRadio} />
                    URL
                </label>
                <button onClick={generateQR}>Generate !</button>
            </div>
        </div>
    );
}



export default QRCodeGen
