import React, { useState } from 'react';
import axios from 'axios';
function QRCodeGen() {
    const [text, setText] = useState('') // Text state
    const [radio, setRadio] = useState('L') // Radio State defaul "L"
    const [qrCodeImage, setQRCodeImage] = useState(null)

    const generateQRCode = async () => {
        const data = {text: text, radio: radio}
        try {
            const response = await axios.post('/generate_qr', data, {headers: {"Content-Type": "application/json"}}); // Send data to backend
            setQRCodeImage(data.img); // Create image URL
        } catch (error) {
            console.error('Error generating QR code:', error);
        }
    };

    const handleText = (event) => {
        setText(event.target.value);
    }

    const handleRadio = (event) => {
        setRadio(event.target.value);
    }

    return (
        <div className = "px-[10%] py-10">
            <h1 className="font-black text-4xl">QR Code Generator</h1>
            <p>Short description</p>
            <div className="text-center grid grod-rows-3">
                <form onSubmit={(e) => {
                          e.preventDefault()
                          generateQRCode()
                      }}>
                    <input type="text" value={text} onChange={handleText}></input>
                    <label>
                        <input type="radio" value="text" checked={radio === 'text'} onChange={handleRadio} />
                        Text
                    </label>
                    <label>
                        <input type="radio" value="url" checked={radio === 'url'} onChange={handleRadio} />
                        URL
                    </label>
                    <button type="submit">Generate !</button>
                </form>    
                { qrCodeImage && (
                    <img src={`data:image/png;base64,${qrCodeImage}`} alt="Generated QR Code"/>
                )}
            </div>
        </div>
    );
}

export default QRCodeGen;
